#include <bits/stdc++.h>
using namespace std;

class Solution {
public:
    int canmake(int i, vector<int>& vals, int target, vector<vector<int>>& dp) {
        if(target == 0) return 1;
        if(i == vals.size()) return 0;
        if(dp[i][target] != -1) return dp[i][target];
        int notPick = canmake(i + 1, vals, target, dp);
        int pick = 0;
        if(target >= vals[i]) pick = canmake(i + 1, vals, target - vals[i], dp);
        return dp[i][target] = (notPick || pick);
    }

    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int m = queries.size(), n = nums.size();
        bool allZero = true;
        for (int x : nums) {
            if (x != 0) { allZero = false; break; }
        }
        if(allZero) return 0;
        for (int k = 1; k <= m; k++) {
            bool poss = true;
            for (int j = 0; j < n; j++) {
                vector<int> vals;
                for (int i = 0; i < k; i++) {
                    if(queries[i][0] <= j && j <= queries[i][1])
                        vals.push_back(queries[i][2]);
                }
                int target = nums[j];
                if(target != 0) {
                    if(vals.empty()) { poss = false; break; }
                    vector<vector<int>> dp(vals.size() + 1, vector<int>(target + 1, -1));
                    if(!canmake(0, vals, target, dp)) { poss = false; break; }
                }
            }
            if(poss) return k;
        }
        return -1;
    }
};

