class Solution {
public:


    bool check(int m, vector<int>&nums, vector<vector<int>>&q){
        int n=nums.size();
         for (int i = 0; i < n; i++) {
            int target = nums[i];
            vector<int> coins;

            for (int j = 0; j < m; j++) {
                int l = q[j][0], r = q[j][1], v = q[j][2];
                if (l <= i && i <= r) {
                    coins.push_back(v);
                }
            }
             unordered_map<int, bool> memo;
            memo[0] = true;
            for (int coin : coins) {
                unordered_map<int, bool> newDp = memo;
                for (auto [sum, _] : memo) {
                    int ns = sum + coin;
                    if (ns > target) continue;
                    newDp[ns] = true;
                }
                memo = newDp;
            }
            if (!memo.count(target)) return false;
        }

        return true;
    }

    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n=nums.size();
        bool f=true;
        for(int i=0;i<n;i++){
            if(nums[i]>0)f=false;
        }
        if(f)return 0;
        bool flag=false;
        int l=1;
        int h=queries.size();
        while(l<=h){
            int m=l+(h-l)/2;
            if(check(m,nums,queries)){
                flag=true;
                h=m-1;
            }
            else l=m+1;
        }
        if(flag)return l;
        return -1;
    }
};

