        for (auto& v: dp) {
            if (!v.back()) {
                return false;
            }
        }
        return true;
    }
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int sz = nums.size(), k = 0, qsz = queries.size();
        vector<vector<bool>> dp(sz);
