            return 0;
        }
        for (int i = 0; i < qsz; i++) {
            auto& q = queries[i];
            for (int j = q[0]; j <= q[1]; j++) {
                if (dp[j].back()) {
                    continue;
                }
                auto& v = dp[j];
                int vsz = v.size();
                for (int k = vsz - 1; k >= q[2]; k--) {
                    dp[j][k] = dp[j][k] || dp[j][k - q[2]];
