class Solution:
    def minZeroArray(self, nums, queries):
        n = len(nums)

        # Initialize dp array with sets, each position can initially be 0
        dp = [set([0]) for _ in range(n)]

        # Check if all numbers are already zero
        allZero = all(num == 0 for num in nums)
        if allZero:
            return 0

        # Process each query
        for k, (l,r,v) in enumerate(queries):

            # Update dp for range [l, r]
            for j in range(l, r + 1):
                # Add shifted values (equivalent to bitset << v)
                new_values = {x + v for x in dp[j]}
                dp[j].update(new_values)
                # Remove values exceeding nums[j]
                dp[j] = {x for x in dp[j] if x <= nums[j]}

            # Check if all target numbers are achievable
            allAchieved = all(nums[j] in dp[j] for j in range(n))
            if allAchieved:
                return k + 1

        return -1

