#include <vector>
using namespace std;

class Solution {
public:
    bool Issum(vector<int>& v, int num) {
        int n = v.size();
        vector<bool> dp(num + 1, false);
        dp[0] = true; 

        for (int val : v) {  
            if (val > num) continue;
            for (int j = num; j >= val; j--) { 
                dp[j] = dp[j] || dp[j - val]; 
            }
        }
        return dp[num]; 
    }

    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = queries.size();
        int p = nums.size();
        int s = 0, e = n;
        int anss = n+1; 

        while (s <= e) {
            int m = (e - s) / 2 + s;

            vector<vector<int>> q(p);
            for (int i = 0; i < m; i++) {
                int a = queries[i][0], b = queries[i][1], c = queries[i][2];
                for (int j = a; j <= b && j < p; j++) { 
                    q[j].push_back(c);
                }
            }

            bool allz = true;
            for (int i = 0; i < p; i++) {
                if (q[i].size() == 0) {
                    if (nums[i] == 0) continue;
                    else {
                        allz = false;
                        break;
                    }
                }
                if (!Issum(q[i], nums[i])) {
                    allz = false;
                    break;
                }
            }

            if (allz) {
                anss = m;
                e = m - 1;
            } else {
                s = m + 1;
            }
        }

        if(anss == n+1)return -1; else return anss;
    }
};

