class Solution {
public:

    bool f(vector<int>& nums, vector<vector<int>>& queries, int mid) {

        int n= nums.size();

     for(int i=0; i<n; i++){
         int req= nums[i];
         vector<bool> dp(req+1, 0);
         dp[0]= true;
        for(int j=0; j<mid; j++){
            int l= queries[j][0];
            int r= queries[j][1];
            int val= queries[j][2];

            if(l<=i && r>=i){
                for(int sum= req; sum>= val; sum--){
                    dp[sum]= dp[sum]|| dp[sum-val];
                }
            }
        }
         if(dp[req]== false)
             return false;
     }
        return true;
    }

    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {

        int ans= -1;
        int n= nums.size();
        int left= 0;
        int right=queries.size();

        while(left<=right){
            int mid= left + (right-left)/2;

            if(f(nums, queries, mid)== true){
                ans= mid;
                right= mid-1;
            }
            else{
               left = mid+1;
            }
        }

        return ans;
    }
};

