import java.util.ArrayList;
import java.util.List;

class Solution {
    public static void main(String[] args) {
//        System.out.println(new Solution().minZeroArray(new int[]{2, 0, 2}, new int[][]{{0, 2, 1}, {0, 2, 1}, {1, 1, 3}}));
        System.out.println(new Solution().minZeroArray(new int[]{10}, new int[][]{{0, 0, 4}, {0, 0, 8}, {0, 0, 1}, {0, 0, 10}, {0, 0, 10}}));
//        System.out.println(new Solution().ableToNullify(new int[]{10}, new List[]{List.of(4, 8)}));
