    public int minZeroArray(int[] nums, int[][] queries) {

        int low = 0;
        int high = queries.length;
        while (low < high) {
            int mid = Math.floorDiv(low + high, 2);

            final List<Integer>[] apply = apply(nums, queries, mid);

            if (ableToNullify(nums, apply)) {
                high = mid;
            } else {
                low = mid + 1;
            }
        }
        if (low == queries.length && !ableToNullify(nums, apply(nums, queries, low))) {
            return -1;
        } else {
            return low;
        }
    }

    private static List<Integer>[] apply(int[] nums, int[][] queries, int mid) {

        List<Integer>[] mem = new List[nums.length];
        for (int i = 0; i < mem.length; i++) {
            mem[i] = new ArrayList<>();
        }
        for (int i = 0; i < mid; i++) {
            var query = queries[i];
            for (int j = query[0]; j <= query[1]; j++) {
                mem[j].add(query[2]);
            }
        }
        return mem;
    }

    boolean ableToNullify(int[] nums, List<Integer>[] lists) {
        for (int i = 0; i < nums.length; i++) {
            if (!subsetSumToK(lists[i], nums[i], 0, new int[lists[i].size()][1001])) {
                return false;
            }
        }
        return true;
    }

    boolean subsetSumToK(List<Integer> list, int target, int ptr, int[][] mem) {
        if (target < 0) return false;
        if (ptr == list.size()) {
            return target == 0;
        }

        if (mem[ptr][target] != 0) {
            return mem[ptr][target] == 1;
        }
        boolean tryUse = subsetSumToK(list, target - list.get(ptr), ptr + 1, mem);
        boolean tryNotUse = subsetSumToK(list, target, ptr + 1, mem);

        mem[ptr][target] = (tryUse || tryNotUse) ? 1 : 2;
        return tryUse || tryNotUse;
    }
}

