class Solution {
public:
    bool solFunc(vector<int>& arr, int i, int target, vector<vector<int>>& mem) {
        if(i==arr.size()) {
            return (target == 0);
        }

        if(mem[i][target] != -1)
            return mem[i][target];

        bool tk=false, nt=false;
        if(target>=arr[i]) {
            tk = solFunc(arr, i+1, target-arr[i], mem);
        }
        nt = solFunc(arr, i+1, target, mem);

        mem[i][target] = (tk | nt);
        return mem[i][target];
    }
    bool check(vector<int>&arr, int target) {
        vector<vector<int>> mem(arr.size(), vector<int>(target+1, -1));
        return solFunc(arr, 0, target, mem);
    }
    bool isPossible(vector<int> nums, vector<vector<int>>& queries, int k) {
        int n = nums.size();
        vector<vector<int>> diff(n);
        for(int i=0; i<k; i++) {
            for(int j=queries[i][0]; j<=queries[i][1]; j++) {
                diff[j].push_back(queries[i][2]);
            }
        }
        for(int i=0; i<n; i++) {
            if(!check(diff[i], nums[i]))
                return false;
        }
        return true;
    }
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int q = queries.size();
        int l=0, r=q, k=-1;
        int mid;
        while(l<=r) {
            mid = l + (r-l)/2;
            // cout<<mid<<" ";
            if(isPossible(nums, queries, mid)) {
                k = mid;
                r = mid-1;
            } else {
                l = mid+1;
            }
        }
        return k;
    }
};

