class Solution {
    public int minZeroArray(int[] nums, int[][] queries) {
        int n=nums.length;
        int q = queries.length;
        int low=0;
        int high=q;
        int ans=-1;
        while(low<=high){
            int mid = (low+high)/2;
            if(check(nums, queries, mid)){
                ans=mid;
                high=mid-1;
            }
            else low = mid+1;
        }

        return ans;
    }

    public boolean check(int[] nums, int[][] queries, int q){
        // int[] temp = Arrays.copyOf(nums);

        int n=nums.length;
        boolean[] ans=new boolean[n];
        // ArrayList<Integer>[] arr=new ArrayList[n];
        // for(int i=0;i<n;i++) arr[i]=new ArrayList<>();
        for(int i=0;i<n;i++){
            int num = nums[i];
            ArrayList<Integer> arr=new ArrayList<>();
            for(int j=0;j<q;j++){
                int l = queries[j][0];
                int r = queries[j][1];
                int val = queries[j][2];
                if(i>=l && i<=r) arr.add(val);
            }
            if(check2(arr, num)) ans[i]=true;
        }
        for(int i=0;i<n;i++){
            if(!ans[i]) return false;
        }
        return true;


    }

    public boolean check2(ArrayList<Integer> arr, int target){
        boolean[] dp = new boolean[target + 1];
        dp[0] = true;

        for (int num : arr) {
            for (int j = target; j >= num; j--) {
                dp[j] = dp[j] || dp[j - num];
            }
        }
        return dp[target];
    }
}

