class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        n = len(nums)
        elements_queries = [[] for _ in range(n)]

        # 
        for j, q in enumerate(queries):
            l, r, val = q
            for i in range(l, r + 1):
                elements_queries[i].append(j)

        max_k = 0
        for i in range(n):
            d_i = nums[i]
            if d_i == 0:
                continue  # k_i0

            q_list = elements_queries[i]
            dp = {0: -1}  # 0-1

            for j in q_list:
                l, r, val = queries[j]
                tmp = {}
                for s in list(dp.keys()):
                    current_max_j = dp[s]
                    new_s = s + val
                    new_max_j = max(current_max_j, j)
                    if new_s > d_i:
                        continue
                    # 
                    if new_s in tmp:
                        if new_max_j < tmp[new_s]:
                            tmp[new_s] = new_max_j
                    else:
                        tmp[new_s] = new_max_j
                # dp
                for s in tmp:
                    if s in dp:
                        if tmp[s] < dp[s]:
                            dp[s] = tmp[s]
                    else:
                        dp[s] = tmp[s]

            if d_i not in dp:
                return -1
            k_i = dp[d_i] + 1  # k
            if k_i > max_k:
                max_k = k_i

        # 
        for num in nums:
            if num != 0:
                break
        else:
            return 0

        return max_k if max_k != 0 else -1

