class Solution {

    int k;

    public int minZeroArray(int[] nums, int[][] queries) {
        // kk-1-1
        // dfs
        // bfs
        // num
        int n = nums.length;

        int result = Integer.MIN_VALUE;
        boolean neg = false;
        for(int i=0; i<n; i++){
            int num = nums[i];
            k = bfs(queries, num, i);
            if(k == -1){
                return -1; // 
            } else{
                result = Math.max(result, k);
            }
        }
        return result;
    }


    private int bfs(int[][] queries, int num, int targetIdx){
        if(num == 0){
            return 0;
        }
        //  
        Queue<Integer> queue = new LinkedList<>();
        queue.offer(num);
        for(int i=0; i<queries.length; i++){
            int[] query = queries[i];
            if(query[0] > targetIdx || query[1] < targetIdx){
                continue;
            } else{
                // 
                int size = queue.size();
                Set<Integer> set = new HashSet<>();
                while(size > 0){
                    int cur = queue.remove();
                    if(cur >= query[2]){
                        int next = cur - query[2];
                        set.add(next);
                        if(next == 0){
                            return i+1;
                        }
                    }
                    set.add(cur);
                    size--;
                }
                for(int s : set){
                    queue.offer(s);
                }
            }
        }
        return -1;
    }

}

