            for k in range(m):
                li, ri, vali = queries[k]
                if li <= i <= ri:
                    tempset = copy.copy(sumSet)
                    for s in tempset:
                        sumSet.add(s + vali)
                if num in sumSet:
                    maxK = max(k, maxK)
                    break  
            if num not in sumSet:
                return -1 

        return maxK + 1 if maxK != -1 else -1

