class Solution {
public:


    bool targetSum(vector<int> &arr,int k){

        if(arr.size() == 0)
            return k == 0;

        int n = arr.size();
        vector<bool> prev(k + 1, false);

         // Base case: If the target sum is 0, we can always achieve it by taking no elements
         prev[0] = true;

         // Base case: If the first element of 'arr' is less than or equal to 'k', set prev[arr[0]] to true
         if (arr[0] <= k) {
             prev[arr[0]] = true;
         }

         // Iterate through the elements of 'arr' and update the DP table
         for (int ind = 1; ind < n; ind++) {
             // Initialize a new row 'cur' to store the current state of the DP table
             vector<bool> cur(k + 1, false);

             // Base case: If the target sum is 0, we can achieve it by taking no elements
             cur[0] = true;

             for (int target = 1; target <= k; target++) {
                 // If we don't take the current element, the result is the same as the previous row
                 bool notTaken = prev[target];

                 // If we take the current element, subtract its value from the target and check the previous row
                 bool taken = false;
                 if (arr[ind] <= target) {
                     taken = prev[target - arr[ind]];
                 }

                 // Store the result in the current DP table row for the current subproblem
                 cur[target] = notTaken || taken;
             }

             // Update 'prev' with the current row 'cur' for the next iteration
             prev = cur;
         }

         // The final result is stored in prev[k]
         return prev[k];
    }

    bool getMinimumK(vector<int> &nums,vector<vector<int>> &queries,int idxQ){
        int n = nums.size();

        vector<vector<int>> avlValues(n);

        for(int q=0;q<=idxQ;q ++){
            int l = queries[q][0], r = queries[q][1], val = queries[q][2];

            for(int i=l;i<=r;i++)
                avlValues[i].push_back(val);
        }
        bool poss = true;

        for(int i=0;i<n;i++){
            poss &= targetSum(avlValues[i], nums[i]);
        }
        return poss;
    }

    bool checkZero(vector<int> &nums){
        return nums == vector<int> (nums.size(),0);
    }

    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {

        if(checkZero(nums))
            return 0;

        int lo = 0,hi = queries.size()-1;

        while (hi - lo > 1) {
            int mid = (hi+lo)/2;


            // Check possible
            if(getMinimumK(nums, queries, mid))
                hi = mid;
            else
                lo = mid;
        }

        if(getMinimumK(nums, queries, lo))
            return lo + 1;
        else if(getMinimumK(nums, queries, hi))
            return hi+1;
        else
            return -1;
    }
};

