class Solution {
    public int minZeroArray(int[] nums, int[][] queries) {
        int[] d = new int[nums.length];
        int min = 0;
        boolean ok = false;
        for(int i = 0; i < nums.length; i++) {
            if(nums[i]==0) continue;

            ok = true;
            List<int[]> q = new ArrayList<>();

            for(int j = 0; j < queries.length; j++) {
                if(i>=queries[j][0] && i<= queries[j][1]) {
                    q.add(new int[]{j, queries[j][2]});
                }
            }

            /*
                 ((pick, notpick) ,target)


            */
            int ans = -1;
            boolean[][] dp = new boolean[q.size()+1][nums[i]+1];
            for(int j = 0; j < dp.length; j++) dp[j][0] = true;

            for(int j = 1; j < dp.length; j++) {
                for(int k = 1; k < dp[j].length; k++) {
                   dp[j][k] = dp[j-1][k];
                   if(k >= q.get(j-1)[1]) {
                       dp[j][k] |= dp[j-1][k-q.get(j-1)[1]];
                   }
                }

                if(dp[j][nums[i]]) {
                    ans = q.get(j-1)[0];
                    break;
                }
            }

            if(ans==-1) {
                return -1;
            }

            // System.out.println(nums[i]+" "+ans);

            min = Math.max(min, ans+1);
        }

        return ok? min: 0;
    }
}

