class Solution {
public:
    int minZeroArray(vector<int>& a, vector<vector<int>>& q) {
        int n = a.size(), nq = q.size();
        int sum = accumulate(a.begin(), a.end(), 0);
        if(!sum)    return 0;
        unordered_map< int, vector<vector<int>> > mp;
        for(int i=0; i<n; i++){
            mp[i+1] = {};
        }
        for(int i=0; i<nq; i++){
            int l = q[i][0];
            int r = q[i][1];
            int val = q[i][2];
            for(int j=l; j <= r; j++){
                mp[j+1].push_back({val, i+1});
            }
            // for(int j=0; j<n; j++){
            //     if(j >= l && j <= r){
            //         mp[j+1].push_back(val);
            //     }
            // }
        }
        int res = INT_MIN;
        for(auto it : mp){
            // vector<int> t = it.second;
            int nt = it.second.size();
            int tgt = a[it.first-1];
            int op = help(tgt, nt, it.second);

            // cout << "tgt = "<<tgt << ", id= "<< it.first-1 <<  endl;
            // for(auto e : it.second){
            //     cout << e[0] << ", ";
            // }
            // cout << " OP = "<< op << endl;
            // cout << "n--n";

            if(op == -1)    
                return -1;
            res = max(res, op);
        }
        // cout << "----n";
        return res;
    }
    int help(int tgt, int n, vector<vector<int>> &v){
        if(!tgt)    return 0;
        if(!n)  return -1;
        vector<vector<int>> dp(n, vector<int>(tgt + 5, -1));
        int op = fun(0, n, 0, tgt, dp, v);
        return (op == -3 ? -1 : op);
    }
    int fun(int id, int n, int sum , int tgt, vector<vector<int>> &dp, vector<vector<int>> &v){
        if(id >= n || sum > tgt)
            return -3;
        if(dp[id][sum] != -1)   
                return dp[id][sum];
        // pick 
        int b = v[id][0];
        if(sum + b == tgt)  return v[id][1];

        int notPick = fun(id+1, n, sum , tgt, dp, v);
        int pick = fun(id+1, n, sum + b, tgt, dp , v);

        if(pick == -3 && notPick == -3){
            return dp[id][sum] = -3;
        }

        if(pick == -3)  return dp[id][sum] = notPick;
        else if(notPick == -3)  return dp[id][sum] = pick;

        return  dp[id][sum] = min(pick, notPick);
    }
};

