class Solution:
    def minZeroArray(self, A: List[int], Q: List[List[int]]) -> int:

        if all(x == 0 for x in A):
            return 0

        def calc(el, arr):
            if el == 0:
                return True
            if not arr:
                return False
            dp = [[False] * (len(arr) + 1) for _ in range(el + 1)]
            for j in range(len(arr) + 1):
                dp[0][j] = True
            for i in range(1, el + 1):
                for j in range(1, len(arr) + 1):
                    dp[i][j] = dp[i][j - 1]
                    if i >= arr[j - 1]:
                        dp[i][j] = dp[i][j] or dp[i - arr[j - 1]][j - 1]
            #print(f'{el}, {dp}')
            return dp[-1][-1]

        def canDo(x):
            arr = defaultdict(list)
            for kk in range(x):
                i, j, v = Q[kk]
                for idx in range(i, j + 1):
                    arr[idx].append(v)
            for i, a in enumerate(A):
                #print(f'eval - {x}, {a}, {arr[i]}')
                if not calc(a, arr[i]):
                    return False
            return True

        # for i in range(1, len(Q) + 1):
        #     if canDo(i):
        #         return i
        # return -1

        l, r = 1, len(Q) + 1
        while l < r:
            m = (l + r) // 2
            if canDo(m):
                r = m
            else:
                l = m + 1
        return l if l <= len(Q) else -1

