class Solution {
public:
int dp[1001][1001];
int rec(vector<int>& nums, vector<vector<int>>& q,int i,int j,int num){

if(num==0) return j-1;
    if(j>=q.size()) return INT_MAX;
    if(dp[j][num]!=-1) return dp[j][num];
    int l=q[j][0];
    int h=q[j][1];
    int val=q[j][2];
    int take=INT_MAX,nottake=INT_MAX;
    if(i>=l&&i<=h){
        if(val<=num){
            take=min(take,rec(nums,q,i,j+1,num-val));
        }
    }
    nottake=min(nottake,rec(nums,q,i,j+1,num));
    return dp[j][num]=min(take,nottake);
}
    int minZeroArray(vector<int>& nums, vector<vector<int>>& q) {
        int n=nums.size();
        int n2=q.size();
