class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size();
        int q = queries.size();
        vector<bool>zeo(n, false);
        int z = 0, lasi = 0;
        for(int i = 0; i < n; ++i){
            if(nums[i] == 0){
                zeo[i] = true;
                ++z;
            }
        }
        if(z == n){
            return 0;
        }
        vector<vector<int>>pos(n);
        vector<vector<bool>>ist(n, vector<bool>(1001, false));
        vector<int> mink(n, -1);
        vector<int>::iterator itr;
        vector<int> cur;
        for(int i = 0; i < q; ++i){

            int st = queries[i][0];
            int en = queries[i][1];
            int dec = queries[i][2];
            for(int j = st; j <= en; ++j){
                cur.clear();
                if(mink[j] != -1 || zeo[j]){
                    continue;
                }
                for(itr = pos[j].begin(); itr!= pos[j].end(); ++itr){
                    if((*itr) + dec <=  nums[j] && (!ist[j][(*itr) + dec])){
                         cur.push_back((*itr) + dec);
                        ist[j][(*itr) + dec] = true;
                    }
                }
                for(itr = cur.begin(); itr != cur.end(); ++itr){
                    if((*itr) == nums[j]){
                        mink[j] = i;
                        ++z;
                        lasi = i;

                        break;
                    }
                    pos[j].push_back(*itr);
                }
                pos[j].push_back(dec);
                if(dec == nums[j] && mink[j] == -1){
                    mink[j] = i;
                    ++z;
                    lasi = i;
                }
            }
            if(z == n){
                return lasi + 1;
            }
        }
        return -1;
    }
};

