bool isSubsetSum(vector<int>& nums, int target) {
    int n = nums.size();
    vector<bool> dp(target + 1, false);
    dp[0] = true;

    for (int num : nums) {
        for (int j = target; j >= num; --j) {
            if (dp[j - num]) dp[j] = true;
        }
    }

    return dp[target];
}

bool isPoss(vector<int>nums,vector<vector<int>>&queries,int k){
    int n = nums.size();
    map<int,vector<int>>mp;
    int cnt = 0;

    for(int i=0;i<=k;i++){
        int l = queries[i][0];
        int r = queries[i][1];
        for(int a=l;a<=r;a++){
            mp[a].push_back(queries[i][2]);
        }
    }

    for(auto it:mp){
        sort(it.second.begin(),it.second.end());
    }
    // if(k==0){
    //     for(auto it:mp[0])cout<<it<<" ";
    // }

    for(int i=0;i<n;i++){
        int sum = nums[i];
        if(isSubsetSum(mp[i],sum))cnt++;
    }

    return cnt>=n;
}

    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
          int n = queries.size();

        int ans = -1;

        int s=0,e=n-1;
        int c = 0;
        for(auto it:nums)if(it<=0)c++;
        if(c==nums.size())return 0;

        while(s<=e){
            int mid = (s + ((e-s)>>1));
            // cout<<s<<' '<<e<<" "<<mid<<" ";
            if(isPoss(nums,queries,mid)){
                ans = mid;
                e = mid-1;
            }
            else {
                s = mid+1;
            }
        }

        return ans == -1 ? -1 : ans+1;
    }
};

