class Solution {
public:
    bool targetPoss(vector<int>&temp,int tar,int ind,int sum,vector<vector<int>>&dp){
        if(ind >= temp.size()){
            if(sum == tar){
                return true;
            }
            return false;
        }
        if(sum > tar){
            return false;
        }
        if(dp[ind][sum] != -1){
            return dp[ind][sum];
        }
        bool ans1 = targetPoss(temp,tar,ind+1,sum+temp[ind],dp);
        bool ans2 = targetPoss(temp,tar,ind+1,sum,dp);
        return dp[ind][sum] =  ans1 || ans2;
    }
    bool solve(vector<int>&nums,int n,vector<vector<int>>&queries,int k){
        vector<vector<int>>temp(n,vector<int>());
        for(int i =0;i<k;i++){
            int u = queries[i][0];
            int v = queries[i][1];
            int val = queries[i][2];
            for(int i=u;i<=v;i++){
                temp[i].push_back(val);
            }
        }
        for(int i =0;i<n;i++){
            int tar = nums[i];
            vector<int>& var = temp[i];
            vector<vector<int>>dp(var.size(),vector<int>(tar+1,-1));
            bool temp1 = targetPoss(var,tar,0,0,dp);
            if(temp1 == false){
                return false;
            }
        }
        return true;

    }
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size();
        int flag =0;
        for(int i =0;i<n;i++){
            if(nums[i] != 0){
                flag =1;
                break;
            }
        }
        if(flag == 0){
            return 0;
        }
        int ans =-1;
        int st =1;
        int end = queries.size();
        while(st <= end){
            int mid = (st + (end-st)/2);
            if(solve(nums,n,queries,mid)){
                ans = mid;
                end = mid-1;
            }
            else{
                st = mid+1;
            }
        }
        return ans;
    }
};

