class Solution {
public:
    int minZeroArray(vector<int>& arr, vector<vector<int>>& queries) {
        int sum = accumulate(arr.begin(), arr.end(), 0);
        if (sum == 0) {
            return 0;
        }
        int ans = 0, n = arr.size();
        vector<int> dp(1001, 0);
        for (int i = 0; i < n; i++) {
            dp[arr[i]] = 1;
            for (int k = 0; k < queries.size(); k++) {
                if (queries[k][0] <= i && queries[k][1] >= i) {
                    int val = queries[k][2];
                    if (dp[val]) {
                        dp[0] = 1;
                        ans = max(ans, k + 1);
                        break;
                    }
                    for (int j = val; j <= arr[i]; j++) {
                        if (dp[j]) {
                            dp[j - val] = 1;
                        }
                    }
                }
            }
            if (!dp[0]) {
                return -1;
            }
            fill(dp.begin(), dp.end(), 0);
        }
        return ans;
    }
};

