class Solution {
    public int minZeroArray(int[] nums, int[][] queries) {
        int top = 0, bot = queries.length, output = -1;
        while(top<=bot){
            int mid = (bot-top)/2 + top;
            int temp[] = nums.clone();
            HashMap<Integer,Integer> cal = new HashMap<>();
            HashMap<Integer,List<Integer>> yo = new HashMap<>();
            HashMap<Integer,List<Integer>> kl = new HashMap<>();
            for(int i=0;i<mid;i++){
                int row[] = queries[i];
                yo.computeIfAbsent(row[0],s->new ArrayList<>()).add(row[2]);
                kl.computeIfAbsent(row[1]+1,s->new ArrayList<>()).add(row[2]);
            }
            boolean check = true;
            HashMap<Integer,Integer> process = new HashMap<>();
            for(int i=0;i<temp.length;i++) { 
                List<Integer> process1 = kl.getOrDefault(i,new ArrayList<>());
                for(int j:process1) { process.put(j,process.getOrDefault(j,0)-1); if(process.get(j) <= 0) process.remove(j); }
                List<Integer> process2 = yo.getOrDefault(i,new ArrayList<>());
                for(int j:process2) { process.put(j,process.getOrDefault(j,0)+1); }
                List<Integer> process3 = new ArrayList<>();
                for(int k:process.keySet()) for(int j=0;j<process.get(k);j++) process3.add(k);
                //System.out.println(i+"   "+mid+"  "+process+"   "+process3+"   "+yo+"   "+kl);
                check &= find(process3,temp[i]);

            }
            if(check) { bot = mid-1; output = mid; }
            else top = mid+1;
        }
        return output;
    }
    public boolean find(List<Integer> nums, int target) {
        boolean[] dp = new boolean[target + 1];
        dp[0] = true;

        for (int num : nums) {
            for (int j = target; j >= num; j--) {
                dp[j] = dp[j] || dp[j - num];
            }
        }
        return dp[target];
    }
}

