class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int m = queries.size(), n = nums.size();
        vector<vector<pair<int,int>>> cover(n);
        for (int j = 0; j < m; j++) {
            int l = queries[j][0], r = queries[j][1], val = queries[j][2];
            for (int i = l; i <= r; i++) {
                cover[i].push_back({j, val});
            }
        }
        auto canAchieveCandidate = [&](int k) -> bool {
            for (int i = 0; i < n; i++) {
                int target = nums[i];
                vector<int> coins;
                for (auto &p : cover[i]) {
                    if (p.first < k)
                        coins.push_back(p.second);
                }
                vector<bool> dp(target + 1, false);
                dp[0] = true;
                for (int coin : coins) {
                    for (int s = target; s >= coin; s--) {
                        if (dp[s - coin])
                            dp[s] = true;
                    }
                }
                if (!dp[target])
                    return false;
            }
            return true;
        };
        int left = 0, right = m, ans = -1;
        while (left <= right) {
            int mid = (left + right) / 2;
            if (canAchieveCandidate(mid)) {
                ans = mid;
                right = mid - 1;
            } else {
                left = mid + 1;
            }
        }
        return ans;
    }
};

