class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        def is_possible(num, new_nums):
            n = len(new_nums)
            dp = [[0 for _ in range(num + 1)] for _ in range(n + 1)]
            dp[0][0] = 1
            dp = [0] * (num + 1)
            dp[0] = 1
            for i in range(1, n + 1):
                new_dp = [0] * (num + 1)
                for j in range(num + 1):
                    new_dp[j] = dp[j]
                    prev = j - new_nums[i - 1]
                    if prev >= 0:
                        new_dp[j] += dp[prev]
                dp = new_dp[::]

            return dp[num] != 0

        def is_valid(idx):
            for i in range(n):
                new_nums = []
                for l, r, val in queries[:idx]:
                    if l <= i <= r:
                        new_nums.append(val)
                if not is_possible(nums[i], new_nums):
                    return False
            return True

        n = len(nums)
        q = len(queries)
        if nums == [0] * n:
            return 0

        lower = 0
        upper = q

        if not is_valid(q):
            return -1

        while lower < upper:
            mid = (lower + upper) // 2

            if is_valid(mid):
                upper = mid
            else:
                lower = mid + 1

        return lower

