class Solution {
    public int minZeroArray(int[] nums, int[][] queries) {
        int n = nums.length,left = 0,right = queries.length;
        if(!canMakeZeroArray(nums,right,queries)){
            return -1;
        }
        while(left <= right){
            int mid = left + (right-left)/2;
            if(canMakeZeroArray(nums,mid,queries)){
                right = mid-1;
            }
            else{
                left = mid+1;
            }
        }
        return left;
    }
    private boolean canMakeZeroArray(int[] nums,int k,int[][] queries){
        int n = nums.length;
        List<List<Integer>> list = new ArrayList<>();
        for(int i=0;i<n;i++){
            list.add(new ArrayList<>());
        }
        for(int i=0;i<k;i++){
            int start = queries[i][0];
            int end = queries[i][1];
            int val = queries[i][2];
            for(int x=start;x<=end;x++){
                list.get(x).add(val);
            }
        }
        for(int i=0;i<n;i++){
            if(nums[i] == 0){
                continue;
            }
            if(!targetSumPossible(list.get(i),nums[i])){
                return false;
            }
        }
        return true;
    }
    private boolean targetSumPossible(List<Integer> arr,int target){
        int n = arr.size();
        boolean[] dp = new boolean[target+1];
        dp[0] = true;
        for(int num:arr){
            for(int j=target;j>=num;j--){
                dp[j] = dp[j] || dp[j-num];
            }
        }
        return dp[target];
    }
}

