class Solution:
    def getSubsets(self, state):
        subset = state
        while subset:
            yield subset
            subset = (subset - 1) & state

    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        @cache
        def dfs(idx, j, num):
            if idx == len(queries):
                if num == 0: return 0
                return len(queries) + 1

            if num == 0:
                return 0

            answer = dfs(idx+1, j, num) + 1

            l, r, v = queries[idx]
            if l <= j and r >= j and v <= num:
                answer = min(answer, dfs(idx+1, j, num-v) + 1)
            return answer


        n = len(nums)
        m = len(queries)
        result = [m+1] * n
        for i in range(n):
            result[i] = dfs(0, i, nums[i])
            dfs.cache_clear()

        answer = max(result)
        if answer > m: return -1
        return answer

