import java.util.*;

class Solution {
    public int minZeroArray(int[] nums, int[][] queries) {
        int ab=0;
        for(int i=0;i<nums.length;i++){
            if(nums[i]==0) ab++;
        }
        if(ab==nums.length) return 0;
        int n = nums.length;
        int qlen = queries.length;

        // Precompute for each index a list of queries (by their order and their value) that affect that index.
        List<int[]>[] affect = new ArrayList[n];
        for (int i = 0; i < n; i++) {
            affect[i] = new ArrayList<>();
        }
        // For each query, add it to all indices in its range.
        for (int qi = 0; qi < qlen; qi++) {
            int l = queries[qi][0], r = queries[qi][1], val = queries[qi][2];
            l = Math.max(l, 0);
            r = Math.min(r, n - 1);
            for (int i = l; i <= r; i++) {
                affect[i].add(new int[]{qi, val});
            }
        }

        // Binary search: find the minimum prefix (first mid queries) that can reduce every nonzero nums[i] to zero.
        int lo = 1, hi = qlen;
        int ans = -1;
        while (lo <= hi) {
            int mid = lo + (hi - lo) / 2;
            boolean allZero = true;
            // Check each position in nums
            for (int i = 0; i < n; i++) {
                if (nums[i] == 0) continue;
                // Gather applicable query values for index i with query index < mid.
                List<Integer> values = new ArrayList<>();
                int total = 0;
                for (int[] qdata : affect[i]) {
                    if (qdata[0] < mid) {
                        values.add(qdata[1]);
                        total += qdata[1];
                    } else {
                        // Since queries are added in order, break early.
                        break;
                    }
                }
                // If available sum is less than needed, it's impossible.
                if (total < nums[i]) {
                    allZero = false;
                    break;
                }

                // Use BitSet DP for subset sum.
                if (!subsetSum(nums[i], values)) {
                    allZero = false;
                    break;
                }
            }
            if (allZero) {
                ans = mid;
                hi = mid - 1;
            } else {
                lo = mid + 1;
            }
        }
        return ans;
    }

    // DP using BitSet: returns true if some subset of values sums exactly to target.
    private boolean subsetSum(int target, List<Integer> values) {
        BitSet dp = new BitSet(target + 1);
        dp.set(0); // sum 0 is always achievable.
        for (int v : values) {
            // Shift dp left by v
            dp.or(shiftLeft(dp, v, target));
            if (dp.get(target)) {
                return true;
            }
        }
        return dp.get(target);
    }

    // Shifts the BitSet dp to the left by v and returns a new BitSet,
    // only keeping indices up to target.
    private BitSet shiftLeft(BitSet dp, int v, int target) {
        BitSet shifted = new BitSet(target + 1);
        for (int i = dp.nextSetBit(0); i >= 0 && i + v <= target; i = dp.nextSetBit(i + 1)) {
            shifted.set(i + v);
        }
        return shifted;
    }
}

