class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& q) {
        int n = nums.size();
        if(*max_element(begin(nums),end(nums)) ==0) return 0;
        set<int> s;
        vector<vector<bool>> v(n);
        for (int i=0;i<n; i++) {
            v[i].resize(nums[i] + 1, false);
            v[i][0] = true;
            if (nums[i] == 0)
                s.insert(i);
        }
        for (int i=0; i<q.size(); i++) {
            int a=q[i][0],b = q[i][1],c = q[i][2];
            for (int j = a; j <= b; j++) {
                if (!s.count(j) && c <= nums[j]) {
                    for (int t = nums[j]; t >= c; t--) {
                        v[j][t] = v[j][t] || v[j][t - c];
                    }
                    if (v[j][nums[j]])
                        s.insert(j);
                }
            }
            if (s.size() == n)
                return i + 1;
        }
        return -1;
    }
};

