class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n=nums.size();
        int miniidx=-1;
        int sz=queries.size();
        int flg=0;
        for(int i=0;i<n;i++){
            if(nums[i]>0)flg=1;
        }
        if(!flg)return 0;
        for(int i=0;i<n;i++){
            int target=nums[i];
            vector<vector<bool>>dp(sz+1,vector<bool>(target+1,false));
            for(int j=0;j<sz;j++)dp[j][0]=true;
            for(int idx=0;idx<sz;idx++){
                for(int t=0;t<=target;t++){
                    if(idx>0)dp[idx][t]=dp[idx-1][t];
                if(queries[idx][0]<=i&&queries[idx][1]>=i&&t>=queries[idx][2]){
                    if(idx>0)
                    dp[idx][t]=(dp[idx][t]||(dp[idx-1][t-queries[idx][2]]));
                    else{
                        dp[idx][t]=(t==queries[idx][2]);
                    }
                }
            }
            }
            int curr=1e9;
            for(int j=0;j<sz;j++){
                if(dp[j][target]){
                    curr=j;
                        break;
                }
            }
            if(curr==1e9)return -1;
            miniidx=max(miniidx,curr);
        }
        if(miniidx==-1)return -1;
        return miniidx+1;
    }
};

