class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size(), q = queries.size(), INF = 1000000000, overall = 0;
        vector<vector<int>> midQueries = queries;
        for (int i = 0; i < n; i++) {
            int target = nums[i];
            vector<int> dp(target + 1, INF);
            dp[0] = 0;
            int earliest = INF;
            for (int j = 0; j < q; j++) {
                int l = midQueries[j][0], r = midQueries[j][1], val = midQueries[j][2];
                if (i >= l && i <= r)
                    for (int s = target - val; s >= 0; s--)
                        if (dp[s] != INF && s + val <= target)
                            dp[s + val] = min(dp[s + val], j + 1);
                if (dp[target] != INF) { earliest = dp[target]; break; }
            }
            if (earliest == INF) return -1;
            overall = max(overall, earliest);
        }
        return overall;
    }
};

