class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n=nums.size(),q=queries.size();
        int l=0,r=q,ans=q+1;
        vector<vector<pair<int,int>>>ic(n);
        for(int i=0;i<q;i++){
            for(int j=queries[i][0];j<=queries[i][1];j++){
                ic[j].push_back({i,queries[i][2]});
            }
        }
        for(int i=0;i<n;i++)sort(ic[i].begin(),ic[i].end());
        function<bool(int, int, int, int, vector<pair<int, int>>&, vector<vector<int>>&) > dpc =
            [&](int i, int cs, int tv, int mid, vector<pair<int, int>>& curr, vector<vector<int>>& dp) -> bool {
                if(cs==tv)return true;
                if(cs>tv)return false;
                if(i>=curr.size())return false;
                if(curr[i].first>=mid)return false;
                if(dp[i][cs]!=-1)return dp[i][cs];
                bool ntk=dpc(i+1,cs,tv,mid,curr,dp);
                bool tk=false;
                if (cs + curr[i].second <= tv)tk =dpc(i+1,cs+curr[i].second,tv,mid,curr,dp);;
                return dp[i][cs]=(tk||ntk);
           };
        auto f=[&](int mid)->bool{
          for(int i=0;i<n;i++){
              auto curr=ic[i];
              vector<vector<int>>dp(curr.size()+1,vector<int>(nums[i]+1,-1));
              if(!dpc(0,0,nums[i],mid,curr,dp))return false;
          }
          return true;
        };
        while(l<=r){
            int mid=(l+r)>>1;
            if(f(mid)){
                ans=mid;
                r=mid-1;
            }else{
                l=mid+1;
            }
        }
        if(ans==q+1)return -1;
        return ans;
    }
};

