class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        n = len(nums)
        m = max(nums)
        if all([nums[i] == 0 for i in range(n)]):
            return 0

        dp = [[False]*(m+1) for _ in range(n)]
        for i in range(n):
            dp[i][0] = True
        count = 1
        for l, r, val in queries:
            for i in range(l, r+1):
                if not dp[i][nums[i]]:
                    for j in range(nums[i], val-1, -1):
                        if dp[i][j-val]:
                            dp[i][j] = True
            completed = True
            for i in range(n):
                if not dp[i][nums[i]]:
                    completed = False
                    break
            if completed:
                return count
            else:
                count += 1
        return -1

