from functools import cache
import math

class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        m = defaultdict(list)
        for x in range(len(queries)):
            i,j,v = queries[x]
            for k in range(i,j+1):
                m[k].append((v, x))

        @cache
        def f(n, i, count, vali):
            if n==0:
                return count
            currVals = m[i]
            if vali >= len(currVals):
                return math.inf
            currVal, currValIndex = currVals[vali]
            currk = math.inf
            diff = n-currVal
            if diff >= 0:
                currk = min(currk, f(diff, i, currValIndex+1, vali+1))
            currk = min(currk, f(n, i, currValIndex+1, vali+1))
            return currk

        k = 0
        for i in range(len(nums)):
            ret = f(nums[i], i, 0, 0)
            if ret == math.inf:
                return -1
            k = max(k, ret)

        return k

