class Solution {
public:
    int dp[1001][1001];
    int f2(int i,int t,vector<int>& a){
        if(t == 0) return 1;
        if(i == a.size()) return 0;
        if(dp[i][t] != -1) return dp[i][t];
        if(a[i] <=t) return dp[i][t] = f2(i+1,t-a[i],a) || f2(i+1,t,a);
        return dp[i][t] = f2(i+1,t,a);
    }
    int f(int mid,vector<vector<int>>& q,vector<int>& nums){
        int n = nums.size();
        vector<int> a[n];
        for(int i=0;i<mid;i++){
            int l = q[i][0] , r = q[i][1] , v = q[i][2];
            //cout<<l<<" "<<" "<<r<<endl;
            for(int j=l;j<=r;j++) a[j].push_back(v);
        }
        // if(mid == 5){
        //     for(int i=0;i<n;i++){
        //         for(auto j:a[i]) cout<<j<<" ";
        //         cout<<endl;
        //     }
        // }
        int ans = 1;
        for(int i=0;i<n;i++) {
            int l = a[i].size();
            int t = nums[i];
            for(int j=0;j<l;j++) for(int k=0;k<=t;k++) dp[j][k] = -1;
            int x = f2(0,t,a[i]);
            //if(mid == 5) cout<<x<<endl;
            ans = (ans&x);
            if(ans == 0) return 0;
        }
        return 1;
    }
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int l = 0 , r= queries.size() , ans = -1;
        while(l<=r){
            int mid = (l+r)/2;
            //cout<<mid<<endl;
            if(f(mid,queries,nums)){
                ans = mid;
                r = mid-1;
            }else l = mid+1;
        }
        return ans;
    }
};

