class Solution {
public:
  bool check(int num, const vector<int>& arr ) {
    int arr_size = arr.size();
    // bool res = solve(arr, num, arr_size);
    // return res;

    vector<vector<bool>> L(num+1, vector<bool>(arr_size+1,false));
    for ( int i = 0; i <= num; ++i ) {
      for ( int j = 0; j <= arr_size; ++j ) {
        if ( i == 0 ) {
          L[i][j] = true;
        }
        else if ( j == 0 ) {
          L[i][j] = false;
        }
        else {
          bool t1 = L[i][j-1];
          bool t2 = false;
          if ( i - arr[j-1] >= 0 ) {
            t2 = L[i - arr[j-1]][j-1];
          }
          bool sp = t1 || t2;
          L[i][j] = sp;
        }        
      }
    }
    int res = L[num][arr_size];
    return res;
  }

  bool check(const vector<int>& nums, const vector<vector<int>>& queries, int k) {
    int nums_size = nums.size();
    vector<vector<int>> Q;
    Q.resize(nums_size);
    for ( int i = 0; i < k; ++i ) {
      int l = queries[i][0];
      int r = queries[i][1];
      int val = queries[i][2];
      for ( int j = l; j <= r; ++j ) {
        Q[j].push_back(val);
      }
    }

    for ( int i = 0; i < nums_size; ++i ) {
      int num = nums[i];
      bool p = check(num, Q[i]);
      if ( !p ) {
        return false;
      }
    }
    return true;
  }

  int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {

    int num_queries = queries.size();
    int lo = 0;
    int hi = num_queries+1;

    while ( lo < hi ) {
      // dbg("============");
      int k = (lo + hi ) / 2;
      // dbg(lo);
      // dbg(hi);
      // dbg(k);      
      bool p = check(nums, queries, k);
      // dbg(p);
      if ( p ) {
        hi = k;
      }
      else {
        lo = k + 1;
      }
    }
    if ( lo == num_queries+1 ) {
      return -1;
    }
    else {
      return lo;
    }
  }  

};

