class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int ans = -1;
        int m = queries.size();
        int n = nums.size();
        int l = 0;
        int r = m;
        // vector<int> cache(1001, m);


        while (l <= r) {
            int mid = l + (r - l) / 2;
            vector<vector<int>> join(n);
            vector<vector<int>> leave(n);

            for (int i = 0; i < mid; i++) {
                join[queries[i][0]].push_back(queries[i][2]);
                leave[queries[i][1]].push_back(queries[i][2]);
            }

            bool pass = true;
            map<int, int> curr;
            for (int i = 0; i < n; i++) {
                for (auto x : join[i]) curr[x] += 1;

                int a = nums[i];
                vector<int> dp(a+1, 0);
                dp[0] = 1;
                for (auto& [k, v] : curr) {
                    if (k > a) break;
                    vector<int> dp2 = dp;
                    for (int j = 1; j <= v; j++) {
                        for (int x = 0; x + k * j <= a; x++) {
                            dp2[x + k * j] |= dp[x];
                        }
                    }
                    swap(dp, dp2);
                }

                if (dp[a] != 1) {
                    pass = false;
                    break;
                }

                for (auto x : leave[i]) curr[x] -= 1;
            }

            if (pass) {
                ans = mid;
                r = mid - 1;
            } else {
                l = mid + 1;
            }
        }

        return ans;
    }
};

