class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:

        n = len(nums)

        def is_zero(k):
            # All of the possible subtractions to be made, sorted by index
            summed = [[] for _ in range(n)]
            for q in queries[:k]:
                l, r, val = q
                for i in range(l, r + 1):
                    summed[i].append(val)

            for i in range(n):
                # Evaluate whether this is possible
                target = nums[i]
                options = summed[i]

                @lru_cache(maxsize=None)
                def can_sum(index, current):
                    # Sum looks good
                    if current == target:
                        return True

                    # Sum is impossible
                    if index >= len(options) or current > target:
                        return False

                    # Skip num
                    if can_sum(index + 1, current):
                        return True

                    # Use num
                    if can_sum(index + 1, current + options[index]):
                        return True

                    return False

                # Can't sum, prefix not possible for one number or more
                if not can_sum(0, 0):
                    return False

            return True

        if all(x == 0 for x in nums):
            return 0


        lo, hi = 1, len(queries)
        ans = -1

        while lo <= hi:
            mid = (lo + hi) // 2
            if is_zero(mid):
                ans = mid
                hi = mid - 1
            else:
                lo = mid + 1

        return ans

