class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        if(accumulate(all(nums),0)==0){
            return 0;
        }
        int n = nums.size();
        int m = queries.size();
        int s=  0,e = m;
        while(s<e){
            int mid = s+(e-s)/2;
            // int pre[n+1];
            vector<int> pre[n+1];
            multiset<int> to_rem[n+1];
            memset(pre,0,sizeof(pre));
            for(int i = 0 ;i<=mid;i++){
                pre[queries[i][0]].push_back(queries[i][2]);
                to_rem[queries[i][1]+1].insert(queries[i][2]);
            }
            bool pos = 1;
            for(int i = 0 ;i<n;i++){
                if(i){
                    for(int j: pre[i-1]){
                        auto it = to_rem[i].find(j);
                        if(it!=to_rem[i].end()){
                            to_rem[i].erase(it);
                        }
                        else{
                            pre[i].push_back(j);
                        }
                    }
                }
                if(to_rem[i].size()!=0){
                    cout<<"FAILURE"<<endl;
                }
                // cout<<mid<<endl;
                // cout<<pre[i]<<endl;
                if(!can_make(pre[i], nums[i])){
                    pos = 0;
                    break;
                }
            }
            if(!pos){
                s = mid+1;
            }
            else{
                e = mid;
            }
        }

        return s==m?-1:++s;
    }
    bool can_make(vector<int>& pre, int val){
        int dp[val+1];
        memset(dp,0,sizeof(dp));
        dp[0] = 1;
        for(int i: pre){
            for(int j = val;j>=i;j--){
                dp[j]|=dp[j-i];
            }
        }
        return dp[val]==1;
    }
};

