                    dp.set(j);
                }
                if (dp.get(target)) {
                    return x[1];
                }
            }
        }

        return -2;
    }
    public int minZeroArray(int[] nums, int[][] queries) {
        Map<Integer, List<int[]>> gfg = new HashMap<>();
        int ans = -1;
        int j = 0;

        for (int[] it : queries) {
            int l = it[0], r = it[1], val = it[2];
            for (int i = l; i <= r; i++) {
                gfg.putIfAbsent(i, new ArrayList<>());
                gfg.get(i).add(new int[]{val, j});
            }
            j++;
        }

        int n = nums.length;
        for (int i = 0; i < n; i++) {
            if (nums[i] == 0) continue;
            int a = canSum(gfg.getOrDefault(i, new ArrayList<>()), nums[i]);
            if (a == -2) return -1;
            ans = Math.max(ans, a);
        }
        return ans + 1;
    }
}

