class Solution {
private:
    bool check(vector<int>& nums, vector<vector<int>>& queries, int k) {
        int n = nums.size();

        for(int i=0; i<n; i++) {
            vector<int> vals;
            for(int j=0; j<k; j++){
                int l = queries[j][0], r = queries[j][1], val = queries[j][2];
                if(l <= i && i <= r)
                    vals.push_back(val);
            }

            vector<bool> dp(nums[i]+1, false);
            dp[0] = true;
            for(int val : vals){
                for(int s=nums[i]; s>=val; s--){
                    dp[s] = dp[s] || dp[s-val];
                }
            }

            if(!dp[nums[i]]) return false;
        }
        return true;
    }
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = queries.size();
        int l = 0, r = n + 1;

        while (l < r) {
            int mid = (l + r) / 2;
            bool result = check(nums, queries, mid);
            // cout << "result: " << result << endl;
            if (result) {
                r = mid;
            } else {
                l = mid + 1;
            }
        }
        return l <= n ? l : -1;
    }
};

