def can_complete(num, vals, i=0) -> bool:
    if num == 0 or sum(vals[i:]) == num:
        return True
    elif num < 0:
        return False
    elif i == len(vals):
        return False

    if can_complete(num-vals[i], vals, i+1) or can_complete(num, vals, i+1):
        return True

    return False


class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        n, m = len(nums), len(queries)

        # Check for zero array
        completed = [False]*n
        for i in range(n):
            if nums[i] == 0:
                completed[i] = True

        if all(completed):
            return 0



        vals = [[] for _ in range(n)]
        k = 0
        for l, r, val in queries:
            k += 1
            for i in range(l, r+1):
                if not completed[i]:
                    vals[i].append(val)
                    if sum(vals[i]) >= nums[i] and can_complete(nums[i], vals[i]):
                        completed[i] = True       

            if all(completed):
                break

        return -1 if not all(completed) else k

