#define ll int
bool isSubsetSum(const vector<int>& vec, int targetSum) {
    int n = vec.size();
    // Create a DP table. dp[i][j] is true if sum j can be achieved with first i elements.
    vector<vector<bool>> dp(n + 1, vector<bool>(targetSum + 1, false));

    // Initialize: A sum of 0 is always possible with 0 elements.
    for (int i = 0; i <= n; i++)
        dp[i][0] = true;

    // Fill the DP table
    for (int i = 1; i <= n; i++) {
        for (int j = 1; j <= targetSum; j++) {
            if (vec[i - 1] > j)
                dp[i][j] = dp[i - 1][j]; // Ignore current element
            else
                dp[i][j] = dp[i - 1][j] || dp[i - 1][j - vec[i - 1]];
        }
    }

    return dp[n][targetSum];
}
