class Solution {

    public int minZeroArray(int[] nums, int[][] queries) {
        int n = nums.length;
        boolean[][] dp;
        boolean isAllZero = true;

        for (int num : nums) {
            if (num != 0) isAllZero = false;
        }

        if (isAllZero) return 0;

        dp = new boolean[n][];
        for (int i = 0; i < n; i++) {
            dp[i] = new boolean[nums[i] + 1];
            dp[i][0] = true;
        }

        for (int mid = 0; mid < queries.length; mid++) {
            int l = queries[mid][0], r = queries[mid][1], val = queries[mid][2];

            for (int i = l; i <= r; i++) {
                for (int j = dp[i].length - 1 - val; j >= 0; j--) {
                    if (dp[i][j]) dp[i][j + val] = true;
                }
            }

            boolean isZeroArray = true;
            for (int i = 0; i < n; i++) {
                if (!dp[i][nums[i]]) {
                    isZeroArray = false;
                    break;
                }
            }

            if (isZeroArray) return mid + 1;
        }

        return -1;
    }
}

