class Solution:
    def minZeroArray(self, nums: List[int], qs: List[List[int]]) -> int:
        n = len(nums)
        nq = len(qs)

        def check(x):
            vs = [[] for _ in range(n)]
            for i in range(x):
                l, r, v = qs[i]
                for j in range(l, r+1):
                    vs[j].append(v)
            for i in range(n):
                b = [0] * (nums[i] + 1)
                b[0] = 1
                for v in vs[i]:
                    for j in range(nums[i], 0, -1):
                        if j - v >= 0 and b[j - v]:
                            b[j] = 1
                if not b[nums[i]]:
                    return False
            return True

        if not check(nq):
            return -1
        if max(nums) == 0:
            return 0
        l, r = 1, nq
        while l < r:
            mi = (l + r) >> 1
            if check(mi):
                r = mi
            else:
                l = mi + 1
        return l

