class Solution
{
    public int minZeroArray(int[] a, int[][] q) {
        int n=a.length;
        BitSet[] dp=new BitSet[n];
        for(int j=0;j<n;j++)
        {
            dp[j]=new BitSet(1001);
            dp[j].set(0);
        }
        boolean p=Arrays.stream(a).allMatch(x->x==0);
        if(p)
            return 0;
        for(int k=0;k<q.length;k++)
        {
            int l=q[k][0],r=q[k][1],v=q[k][2];
            for (int j=l;j<=r;j++)
            {
                BitSet s=new BitSet(1001);
                for (int i=0;i<1001-v;i++)
                    if (dp[j].get(i))
                        s.set(i+v);
                dp[j].or(s);
                dp[j].clear(a[j]+1, 1001);
            }
            boolean x = true;
            for(int j=0;j<n;j++)
                if (!dp[j].get(a[j]))
                {
                    x=false;
                    break;
                }
            if(x)
                return k+1;
        }
        return -1;
    }
}

