class Solution {
    boolean subset(int arr[],int i,int t,Map<String,Boolean> m)
    {
        if(t==0) return true;
        if(i>=arr.length) return false;
        String key=i+","+t;
        if(m.containsKey(key)) return m.get(key);
        if(subset(arr,i+1,t,m))
        {
            m.put(key,true);
            return true;
        }
        if(arr[i]<=t && subset(arr,i+1,t-arr[i],m))
        {
            m.put(key,true);
            return true;
        }
        m.put(key,false);
        return false;
    }
    boolean solve(int nums[],int q[][],int mid)
    {
        int n=nums.length;
        for(int i=0;i<n;i++)
            {
                int t=nums[i];
                if(t==0) continue;
                List<Integer> list=new ArrayList<>();
                for(int j=0;j<=mid;j++)
                    {
                        if(i>=q[j][0] && i<=q[j][1])
                        {
                            list.add(q[j][2]);
                        }
                    }
                int arr[]=new int[list.size()];
                Map<String,Boolean> m=new HashMap<>();
                for(int k=0;k<arr.length;k++)
                    {
                        arr[k]=list.get(k);
                    }
                if(!subset(arr,0,t,m)) return false;
            }
        return true;

    }
    public int minZeroArray(int[] nums, int[][] q) {
        int n=nums.length;
        boolean b=false;
        for(int i=0;i<n;i++)
            {
                if(nums[i]>0)
                {
                    b=true;
                    break;
                }
            }
        if(!b) return 0;
        int s=0;
        int e=q.length-1;
        int ans=-2;
        while(s<=e)
            {
                int mid=s+(e-s)/2;
                if(solve(nums,q,mid))
                {
                    ans=mid;
                    e=mid-1;
                }
                else s=mid+1;
            }
        return ans+1;
    }
}

