class Solution {
public:
    vector<vector<vector<int>>> dp;

    int solve(vector<vector<int>>& q, int x, int id, int i) {
        if(i >= q.size()) return (x == 0) ? i : 1e9;
        if(x == 0) return i;
        if(dp[i][x][id] != -1) return dp[i][x][id];
        int nottake = solve(q, x, id, i + 1);
        int take = 1e9;
        if((q[i][0] <= id && q[i][1] >= id) && x >= q[i][2])
            take = solve(q, x - q[i][2], id, i + 1);
        return dp[i][x][id] = min(take, nottake);
    }

    int minZeroArray(vector<int>& nums, vector<vector<int>>& q) {
        int cnt = 0;
        for(int i=0; i<nums.size(); i++){
            if(nums[i]==0) cnt++;
        }
        if(cnt == nums.size()) return 0;
        int n = nums.size();
        int maxX = *max_element(nums.begin(), nums.end());
        dp.assign(q.size(), vector<vector<int>>(maxX + 1, vector<int>(n, -1)));
        int ans = -1;
        for(int i = 0; i < n; i++) {
            if(nums[i] == 0) continue;
            int a = solve(q, nums[i], i, 0);
            if(a >= 1e9) return -1;
            ans = max(a, ans);
        }
        return ans;
    }
};

