void isPossible(int val, vector<int>& v, int pos, int& ans, vector<bool>& dp) {

    if (pos == v.size()) return;

    for (int i = val-1; i >= 0; i--) {
        if (dp[i]) {
            dp[i + v[pos]] = true;
        }
        if (dp[val]) {
            ans = min(ans, pos + 1);
        }
    }

    isPossible(val, v, pos + 1, ans, dp);
}

